import {
  GO_GUNDAM_START,
  GO_GUNDAM_SUCCESS,
  GO_GUNDAM_FAIL,
  FETCH_GUNDAM_BY_ID_START,
  FETCH_GUNDAM_BY_ID_SUCCESS,
  FETCH_GUNDAM_BY_ID_FAIL
} from 'actionTypes';

import {
  fetchGundams as fetchGundamAPI,
  fetchGundamById as fetchGundamByIdAPI
} from 'api';


export const fetchGundams = () => async dispatch => {
  dispatch({ type: GO_GUNDAM_START });
  try {
    const gundam = await fetchGundamAPI();
    dispatch({
      type: GO_GUNDAM_SUCCESS,
      payload: gundam
    });
  } catch (err) {
    dispatch({
      type: GO_GUNDAM_FAIL,
      payload: err,
      error: true
    });
  }
};



export const fetchGundamById = (id) => async dispatch => {
  dispatch({ type: FETCH_GUNDAM_BY_ID_START });
  try {
    const gundam = await fetchGundamByIdAPI(id);
    dispatch({
      type: FETCH_GUNDAM_BY_ID_SUCCESS,
      payload: gundam
    });
  } catch (err) {
    dispatch({
      type: FETCH_GUNDAM_BY_ID_FAIL,
      payload: err,
      error: true
    });
  }
};
