import React, { Component } from 'react';
import { connect } from 'react-redux';

import { fetchGundamById } from 'actions';
import { getGundamById } from 'selectas';

class GundamDetail extends Component {
  componentDidMount() {
    this.props.fetchGundamById(this.props.params.id);
  }
  renderContent() {
    const { gundam } = this.props;
    let gundamDet = gundam[0] ? gundam[0] : this.renderContent;
    return (
      <div className="col-md-9 col-sm-9 col-lg-9">
        <div className="card">
          <img
            className="card-img-bottom"
            src={gundamDet.image}
            alt={gundamDet.name}
          />
          <div className="card-body">
            <h5 className="card-title pull-right">{gundamDet.price} </h5>
            <h5 className="card-title">{gundamDet.title}</h5>
            <p className="card-text">{gundamDet.info_text}</p>
            <p className="card-text">{gundamDet.series}</p>
            <p className="card-text small">{gundamDet.release_date}</p>
          </div>
        </div>
      </div>
    );
  }
  render() {
    const { gundam } = this.props;
    return (
      <div className="view-container">
        <div className="container">
          <div className="row">
            <div className="col-md-9">{gundam && this.renderContent()}</div>
            <div className="col-md-3">sidebar</div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    gundam: getGundamById(
      state,
      state.gundamDetail.id ? state.gundamDetail.id : state.gundamPage.ids
    )
  };
};

const mapDispatchToProps = {
  fetchGundamById
};

export default connect(mapStateToProps, mapDispatchToProps)(GundamDetail);
