import R from 'ramda';
import { 
  GO_GUNDAM_SUCCESS, 
  FETCH_GUNDAM_BY_ID_SUCCESS 
} from 'actionTypes';

const initialState = {};
export default (state = initialState, { type, payload }) => {
  switch (type) {
    case GO_GUNDAM_SUCCESS:
      const newVal = R.indexBy(R.prop('id'), payload);
      return R.merge(state, newVal);
    case FETCH_GUNDAM_BY_ID_SUCCESS:
      return R.assoc(payload.id, payload, state);
    default:
      return state;
  }
};
