import R from 'ramda';
import { GO_GUNDAM_SUCCESS } from 'actionTypes';

const initialState = {
  ids: []
};
export default (state = initialState, { type, payload }) => {
  switch (type) {
    case GO_GUNDAM_SUCCESS:
      return R.merge(state, {
        ids: R.pluck('id', payload)
      });
    default:
      return state;
  }
};
